import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate, useLocation } from 'react-router-dom';
import { AuthProvider, useAuth } from './AuthContext';
import Sidebar from './components/Sidebar';
import PostGenerator from './components/PostGenerator';
import PostReview from './components/PostReview';
import PostScheduler from './components/PostScheduler';
import Analytics from './components/Analytics';
import Login from './components/Login';
import axios from 'axios';

function AppContent() {
  const [posts, setPosts] = useState([]);
  const [post, setPost] = useState('');
  const { user, setAuthenticatedUser } = useAuth();
  const location = useLocation();
  const [isAuthenticating, setIsAuthenticating] = useState(false);

  useEffect(() => {
    const query = new URLSearchParams(location.search);
    const authenticated = query.get('authenticated') === 'true';
    const userUrn = query.get('user_urn');

    if (authenticated && userUrn && !user) {
      console.log('useEffect - Authenticating with user_urn:', userUrn);
      setIsAuthenticating(true);
      setAuthenticatedUser(userUrn, '');
      axios.get('http://localhost:5000/api/analytics', { params: { user_urn: userUrn } })
        .then((response) => {
          console.log('useEffect - Analytics response:', response.data);
        })
        .catch((error) => {
          console.error('useEffect - Error fetching analytics:', error.response?.data || error);
        })
        .finally(() => {
          setIsAuthenticating(false);
        });
    }
  }, [location, setAuthenticatedUser, user]);

  console.log('AppContent render - user:', user, 'isAuthenticating:', isAuthenticating);

  const onPostsGenerated = (ps) => {
    setPosts(ps);
  };

  const onPostSelected = (ed) => {
    setPost(ed);
  };

  // Wait for authentication to complete before rendering routes
  if (isAuthenticating) {
    return <div>Loading...</div>; // Or a proper loading spinner
  }

  return (
    <div className="flex min-h-screen bg-gray-100">
      <Sidebar />
      <div className="flex-1 p-6 ml-64">
        <Routes>
          <Route path="/login" element={<Login />} />
          <Route
            path="/"
            element={user ? <PostGenerator onPostsGenerated={onPostsGenerated} /> : <Navigate to="/login" />}
          />
          <Route
            path="/review"
            element={user ? <PostReview posts={posts} onPostSelected={onPostSelected} /> : <Navigate to="/login" />}
          />
          <Route
            path="/schedule"
            element={user ? <PostScheduler post={post} /> : <Navigate to="/login" />}
          />
          <Route
            path="/analytics"
            element={user ? <Analytics /> : <Navigate to="/login" />}
          />
          <Route path="*" element={<Navigate to="/login" />} />
        </Routes>
      </div>
    </div>
  );
}

function App() {
  return (
    <AuthProvider>
      <AppContent />
    </AuthProvider>
  );
}

export default App;
