import React, { useState } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider, useAuth } from './AuthContext';
import Sidebar from './components/Sidebar';
import PostGenerator from './components/PostGenerator'; // Renamed from TweetGenerator
import PostReview from './components/PostReview';       // Renamed from TweetReview
import PostScheduler from './components/PostScheduler'; // Renamed from TweetScheduler
import Analytics from './components/Analytics';
import Login from './components/Login';

function ProtectedRoute({ children }) {
  const { isAuthenticated } = useAuth();
  return isAuthenticated ? children : <Navigate to="/login" />;
}

function App() {
  const [posts, setPosts] = useState([]);
  const [post, setPost] = useState('');

  const onPostsGenerated = (ps) => {
    setPosts(ps);
  };

  const onPostSelected = (ed) => {
    setPost(ed);
  };

  return (
    <AuthProvider>
      <div className="flex min-h-screen bg-gray-100">
        <Sidebar />
        <div className="flex-1 p-6 ml-64">
          <Routes>
            <Route path="/login" element={<Login />} />
            <Route
              path="/"
              element={
                <ProtectedRoute>
                  <PostGenerator onPostsGenerated={onPostsGenerated} />
                </ProtectedRoute>
              }
            />
            <Route
              path="/review"
              element={
                <ProtectedRoute>
                  <PostReview posts={posts} onPostSelected={onPostSelected} />
                </ProtectedRoute>
              }
            />
            <Route
              path="/schedule"
              element={
                <ProtectedRoute>
                  <PostScheduler post={post} />
                </ProtectedRoute>
              }
            />
            <Route
              path="/analytics"
              element={
                <ProtectedRoute>
                  <Analytics />
                </ProtectedRoute>
              }
            />
            <Route path="*" element={<Navigate to="/login" />} />
          </Routes>
        </div>
      </div>
    </AuthProvider>
  );
}

export default App;
