import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate, useLocation } from 'react-router-dom';
import { AuthProvider, useAuth } from './AuthContext';
import Sidebar from './components/Sidebar';
import PostGenerator from './components/PostGenerator';
import PostReview from './components/PostReview';
import PostScheduler from './components/PostScheduler';
import Analytics from './components/Analytics';
import Login from './components/Login';
import axios from 'axios';

function ProtectedRoute({ children }) {
  const auth = useAuth();
  console.log('useAuth in ProtectedRoute:', auth);
  if (!auth) {
    console.error('useAuth is undefined in ProtectedRoute');
    return <Navigate to="/login" />;
  }
  const { user } = auth;
  return user ? children : <Navigate to="/login" />;
}

function AppContent() {
  const [posts, setPosts] = useState([]);
  const [post, setPost] = useState('');
  const { setAuthenticatedUser } = useAuth(); // Moved inside a child component
  const location = useLocation();

  useEffect(() => {
    const query = new URLSearchParams(location.search);
    if (query.get('authenticated') === 'true') {
      axios.get('http://localhost:5000/api/analytics', { params: { user_urn: 'temp' } })
        .then(() => {
          setAuthenticatedUser('urn:li:person:temp', 'mock-token');
        })
        .catch((error) => {
          console.error('Error fetching analytics during auth:', error);
        });
    }
  }, [location, setAuthenticatedUser]);

  const onPostsGenerated = (ps) => {
    setPosts(ps);
  };

  const onPostSelected = (ed) => {
    setPost(ed);
  };

  return (
    <div className="flex min-h-screen bg-gray-100">
      <Sidebar />
      <div className="flex-1 p-6 ml-64">
        <Routes>
          <Route path="/login" element={<Login />} />
          <Route
            path="/"
            element={
              <ProtectedRoute>
                <PostGenerator onPostsGenerated={onPostsGenerated} />
              </ProtectedRoute>
            }
          />
          <Route
            path="/review"
            element={
              <ProtectedRoute>
                <PostReview posts={posts} onPostSelected={onPostSelected} />
              </ProtectedRoute>
            }
          />
          <Route
            path="/schedule"
            element={
              <ProtectedRoute>
                <PostScheduler post={post} />
              </ProtectedRoute>
            }
          />
          <Route
            path="/analytics"
            element={
              <ProtectedRoute>
                <Analytics />
              </ProtectedRoute>
            }
          />
          <Route path="*" element={<Navigate to="/login" />} />
        </Routes>
      </div>
    </div>
  );
}

function App() {
  return (
    <AuthProvider>
      <AppContent />
    </AuthProvider>
  );
}

export default App;
