import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../AuthContext';
import axios from 'axios';
import { FaMagic, FaLinkedin } from 'react-icons/fa';

function PostGenerator({ onPostsGenerated }) {
  const [prompt, setPrompt] = useState('');
  const navigate = useNavigate();
  const { user } = useAuth();

  const generatePosts = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/generate-posts', { prompt });
      onPostsGenerated(response.data.variations);
      navigate('/review');
    } catch (error) {
      console.error('Error generating posts:', error);
    }
  };

  return (
    <div className="bg-white p-6 rounded-xl shadow-lg">
      <h2 className="text-2xl font-bold text-blue-800 mb-4 flex items-center">
        <FaMagic className="mr-2" /> Generate LinkedIn Posts
      </h2>
      <p className="text-gray-600 mb-4">Posting as: {user?.urn}</p>
      <div className="mb-6">
        <label className="block text-gray-700 font-medium mb-2 flex items-center">
          <FaLinkedin className="mr-2 text-blue-500" /> Prompt (Optional)
        </label>
        <input
          type="text"
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          placeholder="Enter a creative prompt..."
          className="w-full p-3 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
      </div>
      <button
        onClick={generatePosts}
        className="w-full bg-blue-600 text-white py-3 rounded-md hover:bg-blue-700 transition font-semibold flex items-center justify-center"
      >
        <FaMagic className="mr-2" /> Generate 3 Variations
      </button>
    </div>
  );
}

export default PostGenerator;
