import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../AuthContext';
import axios from 'axios';
import DateTimePicker from 'react-datetime-picker';
import { FaClock, FaCalendarAlt, FaTrash, FaLinkedin } from 'react-icons/fa';

function PostScheduler({ post }) {
  const [tag, setTag] = useState('');
  const [scheduledTime, setScheduledTime] = useState(new Date());
  const [scheduledPosts, setScheduledPosts] = useState([]);
  const navigate = useNavigate();
  const { user } = useAuth();

  const schedulePost = async () => {
    if (!post || !user) return;
    try {
      await axios.post('http://localhost:5000/api/schedule-post', {
        content: post,
        tag,
        scheduled_time: scheduledTime.toISOString().replace('T', ' ').split('.')[0],
        user_urn: user.urn,
      });
      fetchScheduledPosts();
      setTag('');
      setScheduledTime(new Date());
      navigate('/');
    } catch (error) {
      console.error('Error scheduling post:', error);
    }
  };

  const cancelPost = async (postId) => {
    try {
      const response = await axios.post('http://localhost:5000/api/cancel-post', { id: postId });
      if (response.data.success) {
        fetchScheduledPosts();
      } else {
        console.error('Failed to cancel post:', response.data.message);
      }
    } catch (error) {
      console.error('Error canceling post:', error);
    }
  };

  const fetchScheduledPosts = async () => {
    if (!user) return;
    try {
      const response = await axios.get('http://localhost:5000/api/scheduled-posts', {
        params: { user_urn: user.urn },
      });
      setScheduledPosts(response.data.posts);
    } catch (error) {
      console.error('Error fetching scheduled posts:', error);
    }
  };

  useEffect(() => {
    fetchScheduledPosts();
    const interval = setInterval(fetchScheduledPosts, 60000);
    return () => clearInterval(interval);
  }, [user]);

  return (
    <div className="bg-white p-6 rounded-xl shadow-lg">
      <h2 className="text-2xl font-bold text-blue-800 mb-4 flex items-center">
        <FaClock className="mr-2" /> Schedule LinkedIn Post
      </h2>
      {post ? (
        <>
          <div className="mb-6">
            <label className="block text-gray-700 font-medium mb-2">Post to Schedule</label>
            <p className="p-3 bg-gray-50 rounded-md border text-gray-800">{post}</p>
          </div>
          <div className="mb-6">
            <label className="block text-gray-700 font-medium mb-2 flex items-center">
              <FaLinkedin className="mr-2 text-blue-500" /> Tag
            </label>
            <input
              type="text"
              value={tag}
              onChange={(e) => setTag(e.target.value)}
              placeholder="Enter unique tag"
              className="w-full p-3 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>
          <div className="mb-6">
            <label className="block text-gray-700 font-medium mb-2 flex items-center">
              <FaCalendarAlt className="mr-2 text-blue-500" /> Schedule Time
            </label>
            <DateTimePicker
              onChange={setScheduledTime}
              value={scheduledTime}
              className="w-full p-3 border rounded-md"
            />
          </div>
          <button
            onClick={schedulePost}
            className="w-full bg-blue-600 text-white py-3 rounded-md hover:bg-blue-700 transition font-semibold flex items-center justify-center"
          >
            <FaClock className="mr-2" /> Schedule
          </button>
        </>
      ) : (
        <p className="text-gray-600">No post selected for scheduling.</p>
      )}
      <h3 className="text-xl font-semibold text-blue-800 mt-8 mb-4 flex items-center">
        <FaClock className="mr-2" /> Scheduled Posts
      </h3>
      {scheduledPosts.length > 0 ? (
        <ul className="space-y-4">
          {scheduledPosts.map((p) => (
            <li
              key={p.id}
              className="p-4 bg-gray-50 rounded-md border flex justify-between items-center hover:bg-gray-100 transition"
            >
              <div>
                <p className="text-gray-800">{p.content}</p>
                <p className="text-sm text-gray-600">Tag: {p.tag} | Time: {p.scheduled_time}</p>
              </div>
              <button
                onClick={() => cancelPost(p.id)}
                className="bg-red-600 text-white px-4 py-2 rounded-md hover:bg-red-700 transition flex items-center"
              >
                <FaTrash className="mr-2" /> Cancel
              </button>
            </li>
          ))}
        </ul>
      ) : (
        <p className="text-gray-600">No scheduled posts.</p>
      )}
    </div>
  );
}

export default PostScheduler;
